<script>
  export default {
    name: 'AuthenticationResponse',
    data() {
      return {
        count: 100,
        counting: null,
        timeout: null,
        /*Determines when the results are ready to be shown*/
        ready: false
      }
    },
    mounted() {
      /*Begin demo loading*/
      this.startLoading()
    },
    computed: {
      /*Counter for demo loader*/
      countLoaderStyle() {
        return {
          'clip-path': `inset(0 ${this.count}% 0 0 )`
        }
      }
    },
    methods: {
      startLoading() {
        this.timeout = setTimeout(this.doneLoading, 1000)
        this.initCount()
      },
      initCount() {
        this.counting = setInterval(this.increment, 1000)
      },
      increment() {
        this.count = (this.count - 10)
      },
      doneLoading() {
        clearTimeout(this.timeout)
        clearInterval(this.counting)
        this.ready = true
      },
      tryAgain() {
        this.$emit('onTryAgain')
      }
    }
  }
</script>

